package operators;

public class BitwiseOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        
		//Initial Values
		int a = 5;
		int b = 7;
		
		// Bitwise AND
		// 0101 & 0111 = 0101 = 5
		System.out.println("a&b = " + (a & b));
		
		// Bitwise OR
		// 0101 | 0111 = 0111 = 7   
		System.out.println("a|b = " + (a | b));
		
	}

}
